import React, { Component } from 'react';
import { Card, CardTitle, CardText } from 'reactstrap';
import dateFormat from 'dateformat';

class StaffDetail extends Component {
    constructor(props){
        super(props);

    }


    renderStaff(staff) {
        return(
            <div className='col-12 col-md-6 col-lg-4 m-2'>
            <Card key={staff.id}>
                <CardTitle>Họ và tên: {staff.name}</CardTitle>
                <CardText>
                    Ngày sinh: {dateFormat(staff.doB, "dd/mm/yy")}
                    Ngày vào công ty: {dateFormat(staff.startDate, "dd/mm/yy")}
                    Phòng ban: {staff.DEPARTMENTS.name}
                    Số ngày nghỉ còn lại: {staff.annualLeave}
                    Số ngày đã làm thêm: {staff.overTime}
                </CardText>
            </Card>
            </div>
        )
        // console.log(staff)
        // if(staff) {
                
        // }
        // else {
        //     return(
        //         <div>
        //             <p>Bấm vào tên nhân viên để xem thông tin.</p>
        //         </div>
        //     );
        // }
    }

    render() {
        // console.log(this.props.staff);
    //     if(!this.props.staff){
    //         return(
    //         <div>
    //             <p>Bấm vào tên nhân viên để xem thông tin.</p>
    //         </div>);
    //     }

        return (
            // <></>
            <div className="col-12 col-md-6 col-lg-4 mt-2">
                {this.renderStaff(this.props.staff)}
            </div>
        )
    }

}

export default StaffDetail;
