import React, { Component } from 'react';
import { Navbar, NavbarBrand } from 'reactstrap';
import Header from './HeaderComponent';
import Footer from './FooterComponent';
import { STAFFS } from '../shared/staffs';
import Stafflist from './StaffListComponent';
import StaffDetail from './staffdetailComponent';

class Main extends Component {

    constructor(props) {
        super(props);
        this.state = {
            staffs: STAFFS,
            selectedStaff: null
        };
    }

    onStaffSelect(staffId) {
        this.setState({ selectedStaff: staffId});
    }

    render() {
        return(
            <div>
                <Navbar dark>
                    <div className="container">
                        <NavbarBrand href="/"><b>ỨNG DỤNG QUẢN LÝ NHÂN SỰ V1.0</b></NavbarBrand>
                    </div>
                </Navbar>
                <Header />
                <Stafflist staffs={this.state.staffs} onClick={(staffId) => this.onStaffSelect(staffId)} />
                <StaffDetail staff={this.state.staffs.filter((staff) => staff.id === this.state.selectedStaff)[0]} />
                <Footer />
            </div>

        );
    }

}

export default Main;
