import React, { Component } from 'react';
import { Card, CardTitle } from 'reactstrap';
import StaffDetail from './staffdetailComponent';


class Stafflist extends Component {

    constructor(props) {
        super(props);

        this.state = {
            selectedStaff: null
        }
    }

    onStaffSelect(staff) {
        this.setState({ selectedStaff: staff});
    }

    // renderStaff(staff) {
    //     if(staff != null) {
    //         return(
    //             <Card>
    //                 <CardTitle>{staff.name}</CardTitle>
    //             </Card>
    //         );
    //     }
        
    //     else {
    //         return (
    //             <div>
    //                 <p>Bấm vào tên nhân viên để xem thông tin.</p>
    //             </div>
    //         );
    //     }
        
    // }

    render() {
        // console.log(this.state.selectedStaff);
        const list = this.props.staffs.map((staff) => {
            return (
                <div key={staff.id} className="col-12 col-md-6 col-lg-4 mt-2">
                    <Card onClick={() => this.onStaffSelect(staff)}>
                        <CardTitle>{staff.name}</CardTitle>
                    </Card>
                </div>
            );
        });

        return (
            <div className='container'>
                <div className="col-12 col-md-6 col-lg-4 m-2">
                    {list}
                </div>
                <StaffDetail staff={this.state.selectedStaff} />
            </div>
        );
    }

    
}

export default Stafflist;
