import React, { Component } from 'react';
import { Navbar, NavbarBrand } from 'reactstrap';
import Home from './HomeComponent';
import Header from './HeaderComponent';
import Footer from './FooterComponent';
import { STAFFS } from '../shared/staffs';
import Stafflist from './StaffListComponent';
import StaffDetail from './staffdetailComponent';
import { Switch, Route, Redirect } from 'react-router-dom';

class Main extends Component {

    constructor(props) {
        super(props);
        this.state = {
            staffs: STAFFS,
            selectedStaff: null
        };
    }

    // onStaffSelect(staffId) {
    //     this.setState({ selectedStaff: staffId});
    // }

    render() {
        const HomePage = () => {
            return(
                <Home staff={this.state.staffs.filter((staff) => staff.features)[0]} />
            );
        }
        return(
            <div>
                <Header />
                <Switch>
                    <Route path="/home" component={HomePage} />
                    <Route exact="/list" component={() => <Stafflist staffs={this.state.staffs} />} />
                </Switch>
                {/* <Stafflist staffs={this.state.staffs} onClick={(staffId) => this.onStaffSelect(staffId)} />
                <StaffDetail staff={this.state.staffs.filter((staff) => staff.id === this.state.selectedStaff)[0]} /> */}
                <Footer />
            </div>

        );
    }

}

export default Main;
