import React, { Component } from 'react';
import { Card, CardTitle, CardBody, CardText } from 'reactstrap';
import dateFormat from 'dateformat';

class StaffDetail extends Component {
    constructor(props){
        super(props);

        this.state = {
            selectedStaffDetail: this.props.staffdetail
        };
    }

    renderStaff(staff) {
        if(staff !=null) {
            return (
                <div className='col-12 col-md-6 col-lg-4 m-2'>
                    <Card>
                        <CardTitle>Họ và tên: {staff.name}</CardTitle>
                        <CardText>
                            <p>Ngày sinh: {}</p>
                        </CardText>
                    </Card>
                </div>
            );
        }
        else {
            return(
                <div>
                    <p>Bấm vào tên nhân viên để xem thông tin.</p>
                </div>
            );
        }
    }

    render() {
        const staff = this.props.staff;
        console.log(staff);

        const staffdetail = this.renderStaff()

        return (
            <div className='row'>
                {staffdetail}
            </div>
        )
    }

}

export default StaffDetail;
