import React, { Component } from 'react';
import { Card, CardTitle, CardText } from 'reactstrap';
import dateFormat from 'dateformat';

class StaffDetail extends Component {
    constructor(props){
        super(props);

        this.state = {
            selectedStaffDetal: this.props.staffdetail
        }
    }


    renderStaff(staff) {
        if(staff !=null) {
            const dob = dateFormat(staff.doB, "dd/mm/yy");
            const startdate = dateFormat(staff.startDate, "dd/mm/yy");
            const info = staff.map(staff => {
                return(
                    <Card key={staff.id}>
                        <CardTitle>Họ và tên: {staff.name}</CardTitle>
                        <CardText>
                            <p>Ngày sinh: {dob}</p>
                            <p>Ngày vào công ty: {startdate}</p>
                            <p>Phòng ban: {staff.DEPARTMENTS.name}</p>
                            <p>Số ngày nghỉ còn lại: {staff.annualLeave}</p>
                            <p>Số ngày đã làm thêm: {staff.overTime}</p>
                        </CardText>
                    </Card>
                )
            })
            return (
                <div className='col-12 col-md-6 col-lg-4 m-2'>
                    <ul className='list-unstyled'>
                        {info}
                    </ul>
                </div>
            );
        }
        else {
            return(
                <div>
                    <p>Bấm vào tên nhân viên để xem thông tin.</p>
                </div>
            );
        }
    }

    render() {
        const staff = this.props.staff;
        console.log(staff);
        if(staff == null){
            return(
            <div>
                <p>Bấm vào tên nhân viên để xem thông tin.</p>
            </div>);
        }

        return (
            <div className="col-12 col-md-6 col-lg-4 mt-2">
                {this.renderStaff(staff)}
            </div>
        )
    }

}

export default StaffDetail;
